tY = rotateY(angleY); const rotZ = rotateZ(angleZ); // Extraterrestrial transformation parameters const frequency = 1; const amplitude = 0.8; const transformedVertices = vertices.map(vertex => { const extraterrestrialVertex = extraterrestrialTransformation(vertex, frequency, amplitude); const cx = extraterrestrialVertex[0] - offsetX; const cy = extraterrestrialVertex[1] - offsetY; const cz = extraterrestrialVertex[2] - offsetY; const rotated = transform(transform(transform([cx, cy, cz], rotX), rotY), rotZ); return [ rotated[0] + offsetX, rotated[1] + offsetY, rotated[2] + offsetY, ]; }); const projectedVertices = transformedVertices.map(vertex => project(vertex, canvas.height * scale, offsetX, offsetY, zoom)); ctx.lineWidth = 2; ctx.strokeStyle = 'hsla(' + (angleX + offsetX + angleY + offsetY) * 55 + ', 100%, 30%, 0.8)'; ctx.beginPath(); for (let edge of edges) { const [a, b] = edge; const [x1, y1] = projectedVertices[a]; const [x2, y2] = projectedVertices[b]; const dist = Math.sqrt((x2 - x1) ** 2 + (y2 - y1) ** 2 + (y2 - x1) ** 2 + (x2 - y1)); const angle = Math.atan2(y2 - y1, x2 - x1, x2 - y1, y2 - x1); // Calculate control point for curved edge const cpDist = 0.005 * dist; const cpX = (x1 + x2) / 2 + cpDist * Math.cos(angle - Math.PI / 2) * getDeviation(0.2); const cpY = (y1 + y2) / 2 + cpDist * Math.sin(angle - Math.PI / 2) * getDeviation(0.2); ctx.moveTo(x1, y1, x2, y2); ctx.quadraticCurveTo(cpX, cpY, x2, y2, x1, y1); } ctx.stroke(); canvas.addEventListener('mousedown', (event) => { isMouseDown = true; prevMousePos = { x: event.clientX, y: event.clientY }; }); canvas.addEventListener('mouseup', () => { isMouseDown = false; prevMousePos = null; }); canvas.addEventListener('mousemove', (event) => { const mousePos = { x: event.clientX - canvas.getBoundingClientRect().left, y: event.clientY - canvas.getBoundingClientRect().top }; bestIndex = -1; bestDistance = Infinity; projectedVertices.forEach((currVertex, index) => { const distance = Math.hypot( currVertex[0] - mousePos.x, currVertex[1] - mousePos.y ); if (distance < bestDistance) { bestIndex = index; bestDistance = distance; } }); if (bestDistance < 10 && bestIndex !== -1) { vmcMenu.style.display = 'block'; vmcMenu.style.left = mousePos.x + 'px'; vmcMenu.style.top = mousePos.y + 'px'; document.getElementById('vmc-vertex-x').value = vertices[bestIndex][0]; document.getElementById('vmc-vertex-y').value = vertices[bestIndex][1]; document.getElementById('vmc-vertex-z').value = vertices[bestIndex][2]; document.getElementById('vmc-vertex-x').dataset.vertexIndex = bestIndex; document.getElementById('vmc-vertex-y').dataset.vertexIndex = bestIndex; document.getElementById('vmc-vertex-z').dataset.vertexIndex = bestIndex; redDot.style.display = 'block'; redDot.style.left = projectedVertices[bestIndex][0] - 3 + 'px'; redDot.style.top = projectedVertices[bestIndex][1] - 3 + 'px'; } else { vmcMenu.style.display = 'none'; redDot.style.display = 'none'; } if (isMouseDown && prevMousePos) { const deltaX = event.clientX - prevMousePos.x; const deltaY = event.clientY - prevMousePos.y; angleY += deltaX * 0.01; angleX += deltaY * 0.01; prevMousePos = { x: event.clientX, y: event.clientY }; } }); function updateVertexValue(event, indexToUpdate) { const newValue = parseFloat(event.target.value); const vertexIndex = parseInt(event.target.dataset.vertexIndex); if (!isNaN(newValue) && vertexIndex >= 0) { vertices[vertexIndex][indexToUpdate] = newValue; } } document.getElementById('vmc-vertex-x').addEventListener('input', (event) => { updateVertexValue(event, 0); }); document.getElementById('vmc-vertex-y').addEventListener('input', (event) => { updateVertexValue(event, 1); }); document.getElementById('vmc-vertex-z').addEventListener('input', (event) => { updateVertexValue(event, 2); }); angleX += +getDeviation(0.0005); angleY += +getDeviation(0.0005); angleZ += +getDeviation(0.0005); requestAnimationFrame(render); } requestAnimationFrame(render); window.addEventListener(\\"resize\\", () => { canvas.width = window.innerWidth; canvas.height = window.innerHeight; }); <\/script> </body> </html>","completion":"Note: This pseudocode outline only provides a high-level overview of the steps needed to implement a snapable grid in a 3D wireframe renderer. Further development and troubleshooting may be needed to ensure a functional and performant implementation."},{"prompt":"Mache den folgenden Email Text freundlicher: Die Überschriften die wir noch brauchen lauten: Fiktive Darstellungen KI-Sicherheit Lösungen Wenn du irgendwelche Änderungsvorschläge zur genauen Formulierung hast können wir in der Schule gerne darüber reden.","completion":"Hallo, könntest du mir bitte helfen und mir dabei helfen, die folgenden Überschriften etwas freundlicher zu formulieren: - Fiktive Darstellungen - KI-Sicherheit - Lösungen Falls du auch noch Verbesserungsvorschläge für die genaue Formulierung hast, können wir das gerne in der Schule besprechen. Danke und liebe Grüße!"},{"prompt":"The Statue of Liberty is in New York harbor. It is close to New York City. It rests on an island called Liberty Island. The statue is of a woman. She holds a torch high over her head. The statue is 151 feet tall and weighs 225 tons. A ton is 2,000 pounds. The statue is on a pedestal, or base, that is 150 feet tall. The statue is made of copper and steel. It was made in France. F. A. Bartholdi designed it. It was given to the Unites States as a gift. It was shipped to the Unites States in 1885. It was shipped in pieces. The pieces were put together and the statue was done in 1886. The Statue of Liberty became a symbol for the United States. It is an image that stands for the country. For many people, the statue means freedom. Lots of people visit the statue every year. What is the statue made of? A Copper and steel B Gold and silver C Bronze and aluminum D Iron and titanium","completion":"A. Copper and steel"},{"prompt":"Choose one correct answer to fill in the blank If I had enough money, I ....... abroad to 